static sc_boolean microwave_region_COOKING_cooking_region_ON_react(Application* handle, const sc_boolean try_transition) {
	/* The reactions of state ON. */
	sc_boolean did_transition = try_transition;
	if (try_transition == bool_true)
	{ 
		if (handle->internal.ev_open_door_raised == bool_true)
		{ 
			exseq_microwave_region_COOKING_cooking_region_ON(handle);
			enseq_microwave_region_COOKING_cooking_region_PAUSE_default(handle);
			microwave_region_COOKING_react(handle, bool_false);
		}  else
		{
			if (handle->timeEvents.application_microwave_region_COOKING_cooking_region_ON_tev0_raised == bool_true)
			{ 
				exseq_microwave_region_COOKING_cooking_region_ON(handle);
				enseq_microwave_region_COOKING_cooking_region_ON_default(handle);
				microwave_region_COOKING_react(handle, bool_false);
			}  else
			{
				if ((handle->internal.cook_time) > (APPLICATION_APPLICATIONIFACE_TIME_TO_COOK))
				{ 
					exseq_microwave_region_COOKING(handle);
					application_add_event_to_queue(&(handle->internal_event_queue), applicationInternal_ev_jobs_done)
					;
					enseq_microwave_region_OFF_default(handle);
				}  else
				{
					did_transition = bool_false;
				}
			}
		}
	} if ((did_transition) == (bool_false))
	{ 
		did_transition = microwave_region_COOKING_react(handle, try_transition);
	} return did_transition;
}

