static void application_eventqueue_init(application_eventqueue * eq)
{
	eq->push_index = 0;
	eq->pop_index = 0;
	eq->size = 0;
}

static sc_integer application_eventqueue_size(application_eventqueue * eq)
{
	return eq->size;
}

static application_internal_event application_eventqueue_pop(application_eventqueue * eq)
{
	application_internal_event event;
	if(application_eventqueue_size(eq) <= 0) {
		application_internal_event_init(&event, application_invalid_event);
	}
	else {
		event = eq->events[eq->pop_index];
		
		if(eq->pop_index < APPLICATION_EVENTQUEUE_BUFFERSIZE - 1) {
			eq->pop_index++;
		} 
		else {
			eq->pop_index = 0;
		}
		eq->size--;
	}
	return event;
}

static sc_boolean application_eventqueue_push(application_eventqueue * eq, application_internal_event ev)
{
	if(application_eventqueue_size(eq) == APPLICATION_EVENTQUEUE_BUFFERSIZE) {
		return bool_false;
	}
	else {
		eq->events[eq->push_index] = ev;
		
		if(eq->push_index < APPLICATION_EVENTQUEUE_BUFFERSIZE - 1) {
			eq->push_index++;
		}
		else {
			eq->push_index = 0;
		}
		eq->size++;
		
		return bool_true;
	}
}

static void application_internal_event_init(application_internal_event * ev, application_event_name name)
{
	ev->name = name;
}

static void application_add_event_to_queue(application_eventqueue * eq, application_event_name name)
{
	application_internal_event event;
	application_internal_event_init(&event, name);
	application_eventqueue_push(eq, event);
}

static void application_dispatch_event(Application* handle, const application_internal_event * event) {
	switch(event->name) {
		case applicationInternal_ev_open_door:
		{
			handle->internal.ev_open_door_raised = bool_true;
			break;
		}
		case applicationInternal_ev_close_door:
		{
			handle->internal.ev_close_door_raised = bool_true;
			break;
		}
		case applicationInternal_ev_mode:
		{
			handle->internal.ev_mode_raised = bool_true;
			break;
		}
		case applicationInternal_ev_cooking:
		{
			handle->internal.ev_cooking_raised = bool_true;
			break;
		}
		case applicationInternal_ev_jobs_done:
		{
			handle->internal.ev_jobs_done_raised = bool_true;
			break;
		}
		default:
			break;
	}
}

static application_internal_event application_get_next_event(Application* handle)
{
	application_internal_event next_event;
	application_internal_event_init(&next_event, application_invalid_event);
	if(application_eventqueue_size(&(handle->internal_event_queue)) > 0) {
		next_event = application_eventqueue_pop(&(handle->internal_event_queue));
	}
	return next_event;
}
